mapper.select:
SELECT MEMBER_NO, MEMBER_ID, MEMBER_NICKNAME, MEMBER_NAME, MEMBER_TYPE
FROM MEMBER_TBL
WHERE MEMBER_ID = #{memberId} AND MEMBER_PW = #{memberPw}

SELECT COUNT(*)
FROM MEMBER_TBL
WHERE MEMBER_ID = #{memberId}

SELECT * FROM MEMBER_TBL WHERE MEMBER_NAME = #{memberName}

	AND MEMBER_EMAIL = #{memberEmail}


	AND MEMBER_PHONE = #{memberPhone}

SELECT * FROM MEMBER_TBL WHERE MEMBER_ID = #{memberId} AND MEMBER_NAME = #{memberName}

	AND MEMBER_EMAIL = #{memberEmail}


	AND MEMBER_PHONE = #{memberPhone}

mapper.update:
UPDATE MEMBER_TBL 
SET MEMBER_PW = #{memberPw} 
WHERE MEMBER_ID = #{memberId}

UPDATE MEMBER_TBL SET MEMBER_NICKNAME =
#{memberNickname}, MEMBER_PW = #{memberPw}, MEMBER_NAME =
#{memberName}, MEMBER_EMAIL = #{memberEmail}, MEMBER_PHONE =
#{memberPhone}, MEMBER_ADDR = #{memberAddr}
WHERE MEMBER_ID = #{memberId}
