mapper.select:
SELECT p.* , COUNT(r.REVIEW_NO) AS REVIEW_CNT , COUNT(o.ORDER_NO) AS ORDER_CNT FROM PERFUME_TBL p LEFT JOIN REVIEW_TBL r ON p.PERFUME_NO = r.PERFUME_NO LEFT JOIN ORDER_DETAIL_TBL o ON p.PERFUME_NO = o.PERFUME_NO WHERE p.PERFUME_STATUS = 1 AND p.PERFUME_PRICE >= #{startPerfumePrice }
AND p.PERFUME_PRICE <= #{endPerfumePrice }
AND p.P_SCENT_CATEGORY = #{pScentCategory }
AND REGEXP_REPLACE( CONCAT(p.PERFUME_BRAND, p.PERFUME_NAME) , '[[:space:]]' , '') LIKE '%' || REGEXP_REPLACE(#{perfumeSearch}, '[[:space:]]', '') || '%'
GROUP BY p.PERFUME_NO , p.PERFUME_NAME , p.PERFUME_BRAND , p.PERFUME_VOLUME , p.PERFUME_PRICE , p.PERFUME_QUANTITY , p.P_SCENT_CATEGORY , p.P_IMAGE_CATEGORY , p.P_FILENAME , p.P_FILERENAME , p.P_FILEPATH , p.PERFUME_DATE , p.PERFUME_STATUS
ORDER BY p.PERFUME_DATE DESC
ORDER BY ORDER_CNT DESC
ORDER BY REVIEW_CNT DESC
ORDER BY p.PERFUME_PRICE DESC
ORDER BY p.PERFUME_PRICE ASC

SELECT COUNT(*) FROM PERFUME_TBL WHERE PERFUME_STATUS = 1 AND PERFUME_PRICE >= #{startPerfumePrice }
AND PERFUME_PRICE <= #{endPerfumePrice }
AND P_SCENT_CATEGORY = #{pScentCategory }
AND REGEXP_REPLACE( CONCAT(PERFUME_BRAND, PERFUME_NAME) , '[[:space:]]' , '') LIKE '%' || REGEXP_REPLACE(#{perfumeSearch}, '[[:space:]]', '') || '%'

SELECT COUNT(*)
FROM REVIEW_TBL
WHERE PERFUME_NO = #{perfumeNo }

SELECT COUNT(*)
FROM WISH_TBL
WHERE MEMBER_ID = #{memberId } AND PERFUME_NO = #{perfumeNo }

SELECT COUNT(*)
FROM WISH_TBL
WHERE PERFUME_NO = #{perfumeNo }

SELECT WISH_NO
FROM WISH_TBL
WHERE MEMBER_ID = #{memberId } AND PERFUME_NO = #{perfumeNo }

SELECT COUNT(*)
FROM CART_TBL
WHERE MEMBER_ID = #{memberId } AND PERFUME_NO = #{perfumeNo }

SELECT COUNT(*)
FROM ALERT_TBL
WHERE MEMBER_NO = #{memberNo }
		AND ALERT_CATEGORY = '재입고'
		AND PERFUME_NO = #{perfumeNo }
		AND ALERT_DATE IS NULL

mapper.update:
UPDATE ALERT_TBL
SET A_CLICK_STATUS = 1
WHERE ALERT_NO = #{alertNo }
